import java.util.*;interface Main{static void main(String[]x){Scanner c=new Scanner(System.in);double N=0,S=0,T;while(c.hasNext()){c.next();T=c.nextDouble();N+=T;char[]v=c.next().toCharArray();if(v[0]=='P')N-=T;else if(v[0]!='F'){S+=("FDCBA".indexOf(v[0])+(v[1]=='+'?0.5:0))*T;}}System.out.print(S/N);}}